import React, { Component } from "react";
import Lists from './Lists';
import css from "../static/css/index.css";

export default class Todo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: '',
            listItems: []
        };

        this.updateItem = this.updateItem.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
        this.updateInputValue = this.updateInputValue.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.updateListItems = this.updateListItems.bind(this);
    }

    updateItem(item, itemIndex, text) {
        let { items } = this.props;
        item.text = text;
        items[itemIndex] = item;
        this.setState({ listItems: items });
    }

    deleteItem(itemIndex) {
        let { items } = this.props;
        items.splice(itemIndex, 1);
        this.props.updateList(items);
    }

    updateInputValue(event) {
        this.setState({ inputValue: event.target.value });
    }

    handleSubmit(event) {
        event.preventDefault();
        let items = this.state.listItems;
        items.push({ text: event.target['0'].value });
        this.setState({
            listItems: items,
            inputValue: ''
        });
    }

    render() {
        return (
            <div>
                <div>
                    <form className={css.form} onSubmit={this.handleSubmit}>
                        <input type="text" value={this.state.inputValue} onChange={this.updateInputValue}/>
                        <button type="submit"> + </button>
                    </form>
                </div>
                <Lists
                    updateItem={this.updateItem}
                    deleteItem={this.deleteItem}
                    items={this.state.listItems} />
            </div>
        );
    }
};
