import React, { Component } from "react";
import Lists from './Lists';

export default class Todo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newItemValue: '',
            listItems: [
                {
                    description: 'I item',
                    status: true
                },
                {
                    description: 'I second item',
                    status: false
                }
            ]
        };

        this.updateNewItem = this.updateNewItem.bind(this);
        this.submitNewItem = this.submitNewItem.bind(this);
    }

    updateNewItem(event) {
        this.setState({ newItemValue: event.target.value });
    }

    submitNewItem() {
        let { listItems } = this.state;
        listItems.push({
            description: this.state.newItemValue,
            status: false
        });
        this.setState({
            listItems: listItems,
            newItemValue: ''
        });
    }

    render() {
        return (
            <div>
                <div>
                    <form action={this.submitNewItem}>
                        <input type="text" value={this.state.newItemValue} onChange={this.updateNewItem}/>
                        <button type="submit">add</button>
                    </form>
                </div>
                <Lists
                    items={this.state.listItems} />
            </div>
        );
    }
};
