import React, { Component } from "react";

export default class Lists extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.itemStatus = React.createRef();
    }

    render() {
        const { items } = this.props;

        return (
            <div style={{
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center'
            }}>
                {items.map((item, index) => (
                    <div style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        alignItems: 'center'
                    }} key={index.toString()}>
                        <p>{index + 1}</p>
                        <h3>{item.description}</h3>
                        <input type="checkbox" value={item.satus} ref={this.itemStatus} />
                        <button>Delete</button>
                    </div>
                ))}
            </div>
        );
    }
};
