import React, { Component } from "react";

export default class Lists extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.itemStatus = React.createRef();
    }

    render() {
        const { items } = this.props;

        return (
            <div>
                {items.map((item, index) => (
                    <div style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center'
                    }} key={index.toString()}>
                        <p>Todo #{index + 1}</p>
                        <h1>{item.description}</h1>
                        <input type="checkbox" value={item.satus} ref={this.itemStatus} />
                    </div>
                ))}
            </div>
        );
    }
};
