export default (props) => {
    const {
        item,
        itemNumber
    } = props;
    let itemStatus = React.createRef();
    return (
        <div style={{
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'space-between',
            alignItems: 'center'
        }} key={itemNumber.toString()}>
            <p>{itemNumber + 1}</p>
            <h3>{item.description}</h3>
            <input type="checkbox" checked={item.status} ref={itemStatus} onChange={() => props.toggleItem(item, itemNumber)} />
            <button onClick={() => props.deleteItem(item, itemNumber)}>Delete</button>
        </div>
    )
}
