import React, { Component } from 'react';
import css from '../static/css/index.css';

export default class ListItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isEditMode: false,
            inputValue: ''
        };
        this.editItem = this.editItem.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.inputRef = React.createRef();
    }

    editItem(text) {
        this.setState({
            isEditMode: true,
            inputValue: text
        }, () => {
            this.inputRef.current.focus();
        });
    }

    updateInputValue(e){
        this.setState({ inputValue: e.target.value })
    }

    handleSubmit(e) {
        e.preventDefault();
        console.log('OVER HERE!!', e, this.inputRef);
    }

    render() {
        const {
            item,
            itemNumber
        } = this.props;

        return this.state.isEditMode ? (
            <form className={css.form} onSubmit={this.handleSubmit}>
                <input ref={this.inputRef} type="text" value={this.state.inputValue} onChange={this.updateInputValue}/>
                <button type="submit"> update </button>
            </form>
        ) : (
            <div className={css.listItem} key={itemNumber.toString()}>
                <h4>{itemNumber + 1}. </h4>
                <h3 onClick={() => this.editItem(item.text)}>{item.text}</h3>
                <button className={css.doneButton} onClick={() => props.deleteItem(itemNumber)}>done</button>
            </div>
        )
    }    
}
